import React, { useState } from 'react';
import { Card as CardType } from '../types';
import { Card } from './Card';
import { useDeckStore } from '../store/deckStore';

export const MergeBin: React.FC = () => {
  const [cardsToMerge, setCardsToMerge] = useState<CardType[]>([]);
  const [mergedCard, setMergedCard] = useState<CardType | null>(null);
  const { mergeCards, archiveCard } = useDeckStore();

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const cardData = e.dataTransfer.getData('card');
    if (!cardData) return;

    const card: CardType = JSON.parse(cardData);
    
    // Check if card is already in the bin
    if (cardsToMerge.some(c => c.id === card.id)) return;

    // Check if we already have 3 cards
    if (cardsToMerge.length >= 3) return;

    // Check if the card level matches existing cards
    if (cardsToMerge.length > 0 && cardsToMerge[0].level !== card.level) return;

    // Archive the card from its original location
    archiveCard(card);

    setCardsToMerge(prev => [...prev, card]);
  };

  const handleMerge = () => {
    if (cardsToMerge.length !== 3) return;

    const cardIds = cardsToMerge.map(card => card.id);
    const firstCard = cardsToMerge[0];
    
    // Determine the next level
    const nextLevel: CardType['level'] = 
      firstCard.level === 'purple' ? 'yellow' :
      firstCard.level === 'yellow' ? 'red' :
      'red';

    // Create the merged card
    const newMergedCard: CardType = {
      id: `merged-${Date.now()}`,
      name: `${firstCard.name} Mastery`,
      category: firstCard.category,
      level: nextLevel,
      dateEarned: new Date(),
      prerequisites: cardIds,
      description: `Mastered advanced ${firstCard.category} concepts`,
    };

    setMergedCard(newMergedCard);
    setCardsToMerge([]);
  };

  const handleRemoveCard = (cardId: string) => {
    setCardsToMerge(prev => prev.filter(card => card.id !== cardId));
  };

  const handleDragStart = (e: React.DragEvent, card: CardType) => {
    e.dataTransfer.setData('card', JSON.stringify(card));
  };

  const handleDragEnd = (e: React.DragEvent, card: CardType) => {
    // Check if the drop was successful (the card was added to a deck)
    const dropSuccess = e.dataTransfer.dropEffect === 'move';
    if (dropSuccess) {
      if (mergedCard && mergedCard.id === card.id) {
        setMergedCard(null);
      } else {
        setCardsToMerge(prev => prev.filter(c => c.id !== card.id));
      }
    }
  };

  return (
    <div className="fixed bottom-4 right-4 w-80 bg-white rounded-xl shadow-lg p-4">
      <h3 className="text-lg font-bold mb-4">Merge Bin</h3>
      
      <div 
        className={`min-h-[200px] border-2 border-dashed rounded-lg p-4 mb-4 ${
          cardsToMerge.length > 0 ? 'border-blue-500' : 'border-gray-300'
        }`}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        {cardsToMerge.length === 0 && !mergedCard && (
          <p className="text-gray-500 text-center">Drag cards here to merge</p>
        )}
        
        <div className="grid grid-cols-1 gap-2">
          {cardsToMerge.map(card => (
            <div key={card.id} className="relative">
              <Card
                card={card}
                onClick={() => handleRemoveCard(card.id)}
                isDraggable={true}
                onDragStart={(e) => handleDragStart(e, card)}
                onDragEnd={(e) => handleDragEnd(e, card)}
              />
            </div>
          ))}
        </div>

        {mergedCard && (
          <div className="mt-4">
            <p className="text-sm font-semibold mb-2">Merged Card:</p>
            <Card
              card={mergedCard}
              isDraggable={true}
              onDragStart={(e) => handleDragStart(e, mergedCard)}
              onDragEnd={(e) => handleDragEnd(e, mergedCard)}
            />
          </div>
        )}
      </div>

      {cardsToMerge.length === 3 && !mergedCard && (
        <button
          onClick={handleMerge}
          className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
        >
          Merge Cards
        </button>
      )}
    </div>
  );
};
