import React, { useState, useEffect, useRef } from "react";

interface CardData {
  id: string;
  img: string;
  title: string;
  subtitle: string;
  category?: string;
}

interface CardDetailProps {
  card: CardData;
  onClose: () => void;
  onNavigate: (direction: 'left' | 'right') => void;
  relatedCards: CardData[];
}

export default function CardDetail({ card, onClose, onNavigate, relatedCards }: CardDetailProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  // Find current card index in related cards
  useEffect(() => {
    const index = relatedCards.findIndex(c => c.id === card.id);
    setCurrentIndex(index >= 0 ? index : 0);
  }, [card.id, relatedCards]);

  // Handle card flip when center card changes
  useEffect(() => {
    // Flip the card when it comes into focus, then flip back after a moment
    setIsFlipped(true);
    const timer = setTimeout(() => setIsFlipped(false), 600);
    return () => clearTimeout(timer);
  }, [currentIndex]);



  useEffect(() => {
    const keyHandler = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
      if (e.key === 'ArrowLeft') onNavigate('left');
      if (e.key === 'ArrowRight') onNavigate('right');
    };
    
    document.addEventListener('keydown', keyHandler);
    return () => document.removeEventListener('keydown', keyHandler);
  }, [onClose, onNavigate]);

  // Handle click outside to close
  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Swipe/drag navigation
  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
    setDragOffset(0);
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const deltaX = e.clientX - dragStart.x;
    setDragOffset(deltaX);
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    
    const threshold = 50; // Lower threshold for more responsive camera movement
    
    if (Math.abs(dragOffset) > threshold) {
      // Camera movement: drag right = move camera left (show previous card)
      // Camera movement: drag left = move camera right (show next card)
      if (dragOffset > 0 && currentIndex > 0) {
        onNavigate('left'); // Move camera left (previous card comes into view)
      } else if (dragOffset < 0 && currentIndex < relatedCards.length - 1) {
        onNavigate('right'); // Move camera right (next card comes into view)
      }
    }
    
    setIsDragging(false);
    setDragOffset(0);
  };

  // Touch events for mobile
  const handleTouchStart = (e: React.TouchEvent) => {
    const touch = e.touches[0];
    setIsDragging(true);
    setDragStart({ x: touch.clientX, y: touch.clientY });
    setDragOffset(0);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (!isDragging) return;
    const touch = e.touches[0];
    const deltaX = touch.clientX - dragStart.x;
    setDragOffset(deltaX);
  };

  const handleTouchEnd = () => {
    handleMouseUp();
  };

  // Get all 10 cards with their fixed positions in 3D space
  const getAllCardsIn3DSpace = () => {
    return relatedCards.map((card, index) => {
      // Each card has a fixed position in 3D space
      const positionInSpace = index;
      // Camera angle determines viewing perspective
      const relativeToCamera = index - currentIndex;
      
      return {
        ...card,
        absoluteIndex: index,
        positionInSpace,
        relativeToCamera
      };
    });
  };

  const allCardsIn3D = getAllCardsIn3DSpace();

  return (
    <div 
      className="fixed inset-0 z-50 bg-black backdrop-blur-sm flex items-center justify-center"
      onClick={handleBackdropClick}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* Close button */}
      <button
        onClick={(e) => {
          e.stopPropagation();
          onClose();
        }}
        className="absolute top-8 right-8 z-60 w-12 h-12 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300"
      >
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
        </svg>
      </button>

            {/* 3D Canvas with all 10 cards */}
      <div 
        ref={containerRef}
        className="relative w-full h-full flex items-center justify-center cursor-grab active:cursor-grabbing"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
        style={{
          transform: `
            translateX(${dragOffset}px) 
            rotateY(${currentIndex * -10}deg)
          `,
          transition: isDragging ? 'none' : 'transform 0.5s ease-out',
          perspective: '1000px', // Add 3D perspective to the canvas
          transformStyle: 'preserve-3d'
        }}
      >
        {allCardsIn3D.map((cardData) => {
          const { relativeToCamera } = cardData;
          
          // Fixed 3D space positioning - all 10 cards exist in 3D space
          const cardSpacing = 200; // Fixed spacing between cards in 3D world
          const x = relativeToCamera * cardSpacing; // Card's X position in 3D space
          const y = 0; // All cards at same Y level
          const z = Math.abs(relativeToCamera) * -50; // Cards further from camera are deeper in Z
          
          // 3D perspective scaling based on Z-depth
          const distanceFromCamera = Math.abs(relativeToCamera);
          const scale = Math.max(0.5, 1 - distanceFromCamera * 0.1); // Perspective scaling
          const opacity = Math.max(0.3, 1 - distanceFromCamera * 0.15); // Fade with distance
          const zIndex = 50 - distanceFromCamera; // Z-ordering
          
          // 3D rotation - cards angle away from camera
          const rotationY = relativeToCamera * 15; // Y-axis rotation for 3D effect
          const rotationX = Math.abs(relativeToCamera) * 5; // Slight X tilt for depth
          
          return (
                        <div
              key={cardData.id}
              className="absolute transition-all duration-500 ease-out"
              style={{
                transform: `
                  translate3d(${x}px, ${y}px, ${z}px) 
                  scale(${scale}) 
                  rotateY(${rotationY}deg) 
                  rotateX(${rotationX}deg)
                `,
                opacity,
                zIndex,
                transformOrigin: 'center center',
                transformStyle: 'preserve-3d'
              }}
            >
                             {/* Card - all cards are equal objects */}
               <div className="relative w-80 h-80 rounded-2xl overflow-hidden backdrop-blur-xl border border-white/20 shadow-xl shadow-black/40 transition-all duration-500"
                               style={{
                  transform: relativeToCamera === 0 && isFlipped ? 'rotateY(180deg)' : 'rotateY(0deg)',
                  transformStyle: 'preserve-3d'
                }}>
                {/* Background image */}
                <div className="absolute inset-0">
                  <img
                    src={cardData.img}
                    alt={cardData.title}
                    className="w-full h-full object-cover"
                    draggable={false}
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
                </div>

                {/* Content overlay - all cards have same content styling */}
                <div className="absolute inset-0 flex flex-col justify-end p-6">
                  <div className="text-center">
                    <h2 className="font-bold text-white mb-2 text-2xl transition-all duration-500">
                      {cardData.title}
                    </h2>
                    <p className="text-white/80 text-base transition-all duration-500">
                      {cardData.subtitle}
                    </p>
                  </div>
                </div>


              </div>
            </div>
          );
        })}

        {/* Center card additional info */}
        <div className="absolute -bottom-28 left-1/2 -translate-x-1/2 text-center z-60 pointer-events-none">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl px-6 py-4 border border-white/20">
            <p className="text-white/60 text-sm mb-1">Category</p>
            <p className="text-white font-semibold text-base">
              {relatedCards[currentIndex]?.category || relatedCards[currentIndex]?.title}
            </p>
          </div>
        </div>
      </div>



      {/* Card counter */}
      <div className="absolute top-8 left-8 bg-white/10 backdrop-blur-md rounded-xl px-6 py-3 border border-white/20 pointer-events-none">
        <p className="text-white/80 text-base">
          {currentIndex + 1} of {relatedCards.length}
        </p>
      </div>
    </div>
  );
}
