import React, { useState, useEffect, useRef } from "react";

interface CardData {
  id: string;
  img: string;
  title: string;
  subtitle: string;
  category?: string;
}

interface CardDetailProps {
  card: CardData;
  onClose: () => void;
  onNavigate: (direction: 'left' | 'right') => void;
  relatedCards: CardData[];
}

export default function CardDetail({ card, onClose, onNavigate, relatedCards }: CardDetailProps) {
  // 3D Camera system - camera position in 3D space
  const [cameraPosition, setCameraPosition] = useState({ x: 0, y: 0, z: 0, rotationY: 0, rotationX: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  // Initialize camera position to focus on clicked card in 3D space
  useEffect(() => {
    const cardIndex = relatedCards.findIndex(c => c.id === card.id);
    const targetIndex = cardIndex >= 0 ? cardIndex : 0;
    
    // Calculate the 3D position of the target card using same random algorithm
    const seed = targetIndex * 12345;
    const random1 = ((seed * 9301 + 49297) % 233280) / 233280;
    const random2 = (((seed + 1) * 9301 + 49297) % 233280) / 233280;
    const random3 = (((seed + 2) * 9301 + 49297) % 233280) / 233280;
    
    const radius = 400;
    const cardX = (random1 - 0.5) * radius * 2;
    const cardY = (random2 - 0.5) * radius * 2;
    const cardZ = (random3 - 0.5) * radius * 2;
    
    // Position camera to focus on the clicked card from a good viewing angle
    setCameraPosition({
      x: cardX,
      y: cardY - 150, // Position camera above and back from the card
      z: cardZ + 300, // Pull camera back for better view
      rotationY: 0, // Face forward
      rotationX: -20 // Look slightly down at the card
    });
  }, [card.id, relatedCards]);

  // Handle card flip for the card closest to camera
  useEffect(() => {
    setIsFlipped(true);
    const timer = setTimeout(() => setIsFlipped(false), 600);
    return () => clearTimeout(timer);
  }, [cameraPosition.x]);



  useEffect(() => {
    const keyHandler = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
      if (e.key === 'ArrowLeft') {
        // Move camera to previous card position
        const cardSpacing = 200;
        const currentCardIndex = Math.round(cameraPosition.x / cardSpacing);
        if (currentCardIndex > 0) {
          const newIndex = currentCardIndex - 1;
          setCameraPosition({
            x: newIndex * cardSpacing,
            y: -100, // Slightly elevated
            z: 200,  // Moderate distance
            rotationY: newIndex * -5, // Slight rotation
            rotationX: -15 // Slight downward tilt
          });
        }
      }
      if (e.key === 'ArrowRight') {
        // Move camera to next card position
        const cardSpacing = 200;
        const currentCardIndex = Math.round(cameraPosition.x / cardSpacing);
        if (currentCardIndex < relatedCards.length - 1) {
          const newIndex = currentCardIndex + 1;
          setCameraPosition({
            x: newIndex * cardSpacing,
            y: -100, // Slightly elevated
            z: 200,  // Moderate distance
            rotationY: newIndex * -5, // Slight rotation
            rotationX: -15 // Slight downward tilt
          });
        }
      }
    };
    
    document.addEventListener('keydown', keyHandler);
    return () => document.removeEventListener('keydown', keyHandler);
  }, [onClose, cameraPosition.x, relatedCards.length]);

  // Handle click outside to close
  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Swipe/drag navigation
  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
    setDragOffset(0);
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const deltaX = e.clientX - dragStart.x;
    // Apply some resistance to make dragging feel more natural
    const dampedDelta = deltaX * 0.8;
    setDragOffset(dampedDelta);
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    
    const threshold = 60;
    const cardSpacing = 200;
    const currentCardIndex = Math.round(cameraPosition.x / cardSpacing);
    
    // Calculate if we should snap to next/previous card in 3D space
    const shouldSnapToNext = dragOffset < -threshold && currentCardIndex < relatedCards.length - 1;
    const shouldSnapToPrevious = dragOffset > threshold && currentCardIndex > 0;
    
    if (shouldSnapToNext) {
      // Move camera to next card position in 3D space
      const newIndex = currentCardIndex + 1;
      setCameraPosition({
        x: newIndex * cardSpacing,
        y: -100, // Slightly elevated
        z: 200,  // Moderate distance
        rotationY: newIndex * -5, // Slight rotation
        rotationX: -15 // Slight downward tilt
      });
    } else if (shouldSnapToPrevious) {
      // Move camera to previous card position in 3D space
      const newIndex = currentCardIndex - 1;
      setCameraPosition({
        x: newIndex * cardSpacing,
        y: -100, // Slightly elevated
        z: 200,  // Moderate distance
        rotationY: newIndex * -5, // Slight rotation
        rotationX: -15 // Slight downward tilt
      });
    }
    
    setIsDragging(false);
    setDragOffset(0);
  };

  // Touch events for mobile
  const handleTouchStart = (e: React.TouchEvent) => {
    const touch = e.touches[0];
    setIsDragging(true);
    setDragStart({ x: touch.clientX, y: touch.clientY });
    setDragOffset(0);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (!isDragging) return;
    const touch = e.touches[0];
    const deltaX = touch.clientX - dragStart.x;
    // Apply some resistance to make dragging feel more natural
    const dampedDelta = deltaX * 0.8;
    setDragOffset(dampedDelta);
  };

  const handleTouchEnd = () => {
    handleMouseUp();
  };

  // All cards exist as fixed objects in 3D space with their calculated positions
  const allCardsIn3D = relatedCards.map((cardData, index) => {
    // Calculate 3D position for each card
    const seed = index * 12345;
    const random1 = ((seed * 9301 + 49297) % 233280) / 233280;
    const random2 = (((seed + 1) * 9301 + 49297) % 233280) / 233280;
    const random3 = (((seed + 2) * 9301 + 49297) % 233280) / 233280;
    
    const radius = 400;
    const x = (random1 - 0.5) * radius * 2;
    const y = (random2 - 0.5) * radius * 2;
    const z = (random3 - 0.5) * radius * 2;
    
    return {
      ...cardData,
      absoluteIndex: index,
      positionInSpace: index,
      x3d: x,
      y3d: y,
      z3d: z
    };
  });

  // Function to find nearest cards to current camera position
  const findNearestCard = (direction: 'left' | 'right') => {
    const currentCard = allCardsIn3D.find(card => card.id === card.id) || allCardsIn3D[0];
    
    // Find all other cards and sort by distance from current camera position
    const otherCards = allCardsIn3D.filter(card => card.id !== currentCard.id);
    
    const sortedByDistance = otherCards.sort((a, b) => {
      const distA = Math.sqrt(
        Math.pow(a.x3d - cameraPosition.x, 2) +
        Math.pow(a.y3d - cameraPosition.y, 2) +
        Math.pow(a.z3d - cameraPosition.z, 2)
      );
      const distB = Math.sqrt(
        Math.pow(b.x3d - cameraPosition.x, 2) +
        Math.pow(b.y3d - cameraPosition.y, 2) +
        Math.pow(b.z3d - cameraPosition.z, 2)
      );
      return distA - distB;
    });
    
    // Return the closest card (for now, ignore direction)
    return sortedByDistance[0] || allCardsIn3D[0];
  };

  return (
    <div 
      className="fixed inset-0 z-50 bg-black backdrop-blur-sm flex items-center justify-center"
      onClick={handleBackdropClick}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
      style={{
        perspective: '1200px', // 3D perspective for the entire scene
        perspectiveOrigin: 'center center'
      }}
    >
      {/* Close button */}
      <button
        onClick={(e) => {
          e.stopPropagation();
          onClose();
        }}
        className="absolute top-8 right-8 z-60 w-12 h-12 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300"
      >
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
        </svg>
      </button>

            {/* 3D Canvas with all 10 cards */}
      <div 
        ref={containerRef}
        className="relative w-full h-full flex items-center justify-center cursor-grab active:cursor-grabbing"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
        style={{
          transform: `
            translateX(${dragOffset - cameraPosition.x}px)
            translateY(${cameraPosition.y}px)
            translateZ(${cameraPosition.z}px)
            rotateX(${cameraPosition.rotationX}deg)
            rotateY(${cameraPosition.rotationY + (dragOffset * -0.02)}deg)
          `,
          transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94)',
          transformStyle: 'preserve-3d'
        }}
      >
        {allCardsIn3D.map((cardData) => {
          const { positionInSpace } = cardData;
          
          // Scatter cards randomly in true 3D space using seeded random based on card index
          const seed = positionInSpace * 12345; // Use card index as seed for consistent positioning
          const random1 = ((seed * 9301 + 49297) % 233280) / 233280;
          const random2 = (((seed + 1) * 9301 + 49297) % 233280) / 233280;
          const random3 = (((seed + 2) * 9301 + 49297) % 233280) / 233280;
          const random4 = (((seed + 3) * 9301 + 49297) % 233280) / 233280;
          const random5 = (((seed + 4) * 9301 + 49297) % 233280) / 233280;
          
          // Scatter cards in a 3D sphere around the origin
          const radius = 400; // Sphere radius
          const x = (random1 - 0.5) * radius * 2; // -400 to +400
          const y = (random2 - 0.5) * radius * 2; // -400 to +400  
          const z = (random3 - 0.5) * radius * 2; // -400 to +400
          
          // Calculate distance from camera position for effects
          const dx = x - cameraPosition.x;
          const dy = y - cameraPosition.y;
          const dz = z - cameraPosition.z;
          const distanceFromCamera = Math.sqrt(dx*dx + dy*dy + dz*dz) / 100; // Normalize distance
          
          // 3D perspective effects based on distance from camera
          const scale = Math.max(0.2, 1 - distanceFromCamera * 0.1); // Perspective scaling
          const opacity = Math.max(0.1, 1 - distanceFromCamera * 0.15); // Fade with distance
          const zIndex = Math.round(50 - distanceFromCamera * 5); // Z-ordering
          
          // Random rotation for each card in 3D space
          const rotationX = (random4 - 0.5) * 60; // Random X rotation -30 to +30 degrees
          const rotationY = (random5 - 0.5) * 60; // Random Y rotation -30 to +30 degrees
          
          return (
                        <div
              key={cardData.id}
              className="absolute transition-all duration-500 ease-out"
              style={{
                transform: `
                  translate3d(${x}px, ${y}px, ${z}px) 
                  scale(${scale}) 
                  rotateY(${rotationY}deg) 
                  rotateX(${rotationX}deg)
                `,
                opacity,
                zIndex,
                transformOrigin: 'center center',
                transformStyle: 'preserve-3d'
              }}
            >
                             {/* Card - all cards are equal objects */}
               <div className="relative w-80 h-80 rounded-2xl overflow-hidden backdrop-blur-xl border border-white/20 shadow-xl shadow-black/40 transition-all duration-500"
                               style={{
                  transform: Math.round(cameraPosition.x / 200) === cardData.absoluteIndex && isFlipped ? 'rotateY(180deg)' : 'rotateY(0deg)',
                  transformStyle: 'preserve-3d'
                }}>
                {/* Background image */}
                <div className="absolute inset-0">
                  <img
                    src={cardData.img}
                    alt={cardData.title}
                    className="w-full h-full object-cover"
                    draggable={false}
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
                </div>

                {/* Content overlay - all cards have same content styling */}
                <div className="absolute inset-0 flex flex-col justify-end p-6">
                  <div className="text-center">
                    <h2 className="font-bold text-white mb-2 text-2xl transition-all duration-500">
                      {cardData.title}
                    </h2>
                    <p className="text-white/80 text-base transition-all duration-500">
                      {cardData.subtitle}
                    </p>
                  </div>
                </div>


              </div>
            </div>
          );
        })}

        {/* Current card info */}
        <div className="absolute -bottom-28 left-1/2 -translate-x-1/2 text-center z-60 pointer-events-none">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl px-6 py-4 border border-white/20">
            <p className="text-white/60 text-sm mb-1">Viewing</p>
            <p className="text-white font-semibold text-base">
              {relatedCards[Math.round(cameraPosition.x / 200)]?.title || 'Card'}
            </p>
          </div>
        </div>
      </div>



      {/* Card counter */}
      <div className="absolute top-8 left-8 bg-white/10 backdrop-blur-md rounded-xl px-6 py-3 border border-white/20 pointer-events-none">
        <p className="text-white/80 text-base">
          {Math.round(cameraPosition.x / 200) + 1} of {relatedCards.length}
        </p>
      </div>
    </div>
  );
}
