import React, { useState } from 'react';
import { useDeckStore } from './store/deckStore';
import { Deck } from './components/Deck';
import { MergeBin } from './components/MergeBin';
import { MergeLog } from './components/MergeLog';
import { DECK_TIERS } from './types';

const App: React.FC = () => {
  const { decks, activeDeckId, setActiveDeck, upgradeDeck, shuffleAll } = useDeckStore();
  const [showMergeLog, setShowMergeLog] = useState(false);

  const handleUpgrade = (deckId: string, currentTier: string) => {
    const tiers = Object.keys(DECK_TIERS);
    const currentIndex = tiers.indexOf(currentTier);
    if (currentIndex < tiers.length - 1) {
      upgradeDeck(deckId, tiers[currentIndex + 1] as any);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-800">Deck Cards</h1>
          <div className="flex gap-2">
            <button
              onClick={() => setShowMergeLog(!showMergeLog)}
              className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 transition-colors"
              title="Toggle merge history"
            >
              Merge Log
            </button>
            <button
              onClick={shuffleAll}
              className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
              title="Reset all decks to original sample data"
            >
              Shuffle All Decks
            </button>
          </div>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {decks.map((deck) => (
            <Deck
              key={deck.id}
              deck={deck}
              onUpgrade={() => handleUpgrade(deck.id, deck.tier)}
            />
          ))}
        </div>
        
        <div className="mt-6">
          <MergeBin />
        </div>
      </div>
      {showMergeLog && <MergeLog />}
    </div>
  );
};

export default App;
