import React from 'react';
import { useDeckStore } from '../store/deckStore';

interface MergeLogProps {
  onClose: () => void;
}

export const MergeLog: React.FC<MergeLogProps> = ({ onClose }) => {
  const { mergeLog } = useDeckStore();

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-lg p-4 max-h-[80vh] w-96 relative">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-sm font-semibold">Merge History</h3>
          <button
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700"
          >
            ✕
          </button>
        </div>
        <div className="space-y-3 overflow-y-auto max-h-[calc(80vh-4rem)]">
          {mergeLog.map((entry, index) => (
            <div key={index} className="text-xs border-b border-gray-100 pb-3">
              <div className="text-gray-500 mb-2">
                {new Date(entry.timestamp).toLocaleString()}
              </div>
              <div className="flex flex-col gap-1">
                <div className="flex items-center gap-1 text-gray-600">
                  {entry.sourceCards.map((card, i) => (
                    <span key={i}>
                      {card.name}
                      {i < entry.sourceCards.length - 1 ? ' + ' : ''}
                    </span>
                  ))}
                </div>
                <div className="text-blue-600 font-medium">
                  → {entry.resultCard.name}
                </div>
              </div>
            </div>
          ))}
          {mergeLog.length === 0 && (
            <div className="text-gray-400 text-xs">No merge history yet</div>
          )}
        </div>
      </div>
    </div>
  );
};
