import React, { useState } from "react";
import "./InfiniteCanvas.css";

// Base card templates
const cardTemplates = [
  { img: "/assets/thumbnails/mathematics.svg", title: "Mathematics" },
  { img: "/assets/thumbnails/programming.svg", title: "Programming" },
  { img: "/assets/thumbnails/languages.svg", title: "Languages" },
  { img: "/assets/thumbnails/history.svg", title: "History" },
  { img: "/assets/thumbnails/quantum-mechanics.svg", title: "Quantum Mechanics" },
  { img: "/assets/thumbnails/chemistry.svg", title: "Chemistry" },
  { img: "/assets/thumbnails/geometry.svg", title: "Geometry" },
  { img: "/assets/thumbnails/music.svg", title: "Music" },
  { img: "/assets/thumbnails/astronomy.svg", title: "Astronomy" },
  { img: "/assets/thumbnails/finance.svg", title: "Finance" },
  { img: "/assets/thumbnails/technology.svg", title: "Technology" },
  { img: "/assets/thumbnails/health.svg", title: "Health" },
  { img: "/assets/thumbnails/sports.svg", title: "Sports" },
  { img: "/assets/thumbnails/art.svg", title: "Art" },
  { img: "/assets/thumbnails/design.svg", title: "Design" },
  { img: "/assets/thumbnails/business.svg", title: "Business" },
  { img: "/assets/thumbnails/psychology.svg", title: "Psychology" },
  { img: "/assets/thumbnails/social.svg", title: "Social" },
  { img: "/assets/thumbnails/cooking.svg", title: "Cooking" },
  { img: "/assets/thumbnails/travel.svg", title: "Travel" },
];

// Generate many random cards to fill the screen
const generateCards = (count: number) => {
  const cards = [];
  for (let i = 0; i < count; i++) {
    const template = cardTemplates[Math.floor(Math.random() * cardTemplates.length)];
    cards.push({
      id: i + 1,
      img: template.img,
      title: template.title,
    });
  }
  return cards;
};

// Generate 200 cards for extensive coverage
const cards = generateCards(200);

export default function InfiniteCanvas() {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [start, setStart] = useState({ x: 0, y: 0 });

  function onMouseDown(e: React.MouseEvent) {
    setDragging(true);
    setStart({ x: e.clientX - offset.x, y: e.clientY - offset.y });
  }
  function onMouseMove(e: React.MouseEvent) {
    if (!dragging) return;
    setOffset({ x: e.clientX - start.x, y: e.clientY - start.y });
  }
  function onMouseUp() {
    setDragging(false);
  }

  return (
    <div
      className="InfiniteCanvas"
      onMouseDown={onMouseDown}
      onMouseMove={onMouseMove}
      onMouseUp={onMouseUp}
    >
      <div
        className="CardGrid"
        style={{ transform: `translate(${offset.x}px, ${offset.y}px)` }}
      >
        {cards.map((c) => (
          <div key={c.id} className="DeckCard">
            <img src={c.img} alt={c.title} />
            <div className="title">{c.title}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
