import React, { useState } from 'react';
import { Card as CardType } from '../types';
import { Card } from './Card';
import { useDeckStore } from '../store/deckStore';

export const MergeBin: React.FC = () => {
  const { mergeCards, archiveCard } = useDeckStore();
  const [cards, setCards] = useState<CardType[]>([]);
  const [mergedCard, setMergedCard] = useState<CardType | null>(null);
  const [isDragOver, setIsDragOver] = useState(false);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const cardData = e.dataTransfer.getData('card');
    if (!cardData) return;

    try {
      const card: CardType = JSON.parse(cardData);
      
      // Don't add if card is already in the bin
      if (cards.some(c => c.id === card.id)) return;
      
      // Don't add if we already have 3 cards
      if (cards.length >= 3) return;
      
      // Don't add if the card level doesn't match existing cards
      if (cards.length > 0 && cards[0].level !== card.level) return;

      // Archive the card and add it to the bin
      archiveCard(card);
      setCards(prev => [...prev, card]);
    } catch {
      // Invalid card data, ignore
    }
  };

  const handleMerge = () => {
    if (cards.length === 3) {
      const firstCard = cards[0];
      const nextLevel: CardType['level'] =
        firstCard.level === 'purple'
          ? 'yellow'
          : firstCard.level === 'yellow'
          ? 'red'
          : 'red';

      const newMergedCard: CardType = {
        id: `merged-${Date.now()}`,
        name: `${firstCard.name} Mastery`,
        category: firstCard.category,
        level: nextLevel,
        dateEarned: new Date(),
        prerequisites: cards.map(card => card.id),
        description: `Mastered advanced ${firstCard.category} concepts`,
      };

      mergeCards(cards.map(card => card.id));
      setCards([]);
      setMergedCard(newMergedCard);
    }
  };

  const handleRemoveCard = (cardId: string) => {
    setCards(prev => prev.filter(card => card.id !== cardId));
  };

  const handleDragStart = (e: React.DragEvent, card: CardType) => {
    e.dataTransfer.setData('card', JSON.stringify(card));
    e.dataTransfer.effectAllowed = 'move';
  };

  return (
    <div className="fixed bottom-4 right-4">
      <div
        className={`bg-white rounded-lg shadow-lg p-4 transition-colors duration-200 ${
          isDragOver ? 'bg-blue-50' : ''
        }`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <h3 className="text-sm font-semibold mb-2">Merge Bin</h3>
        <div className="flex gap-2">
          {cards.map((card) => (
            <div key={card.id} className="relative">
              <Card
                card={card}
                onClick={() => handleRemoveCard(card.id)}
                isDraggable={true}
                onDragStart={(e) => handleDragStart(e, card)}
              />
            </div>
          ))}
          {Array.from({ length: 3 - cards.length }).map((_, index) => (
            <div
              key={index}
              className="w-20 h-28 border-2 border-dashed border-gray-300 rounded-lg flex items-center justify-center"
            >
              <span className="text-gray-400 text-xs">Drop card</span>
            </div>
          ))}
        </div>
        {cards.length === 3 && (
          <button
            onClick={handleMerge}
            className="mt-2 w-full bg-blue-500 text-white py-1 rounded hover:bg-blue-600 transition-colors text-xs"
          >
            Merge Cards
          </button>
        )}
        {mergedCard && (
          <div className="mt-2">
            <h4 className="text-xs font-medium mb-1">Merged Card:</h4>
            <div className="flex justify-center">
              <Card
                card={mergedCard}
                isDraggable={true}
                onDragStart={(e) => handleDragStart(e, mergedCard)}
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
