import React, { useState, useEffect, useRef } from "react";

interface CardData {
  id: string;
  img: string;
  title: string;
  subtitle: string;
  category?: string;
}

interface CardDetailProps {
  card: CardData;
  onClose: () => void;
  onNavigate: (direction: 'left' | 'right') => void;
  relatedCards: CardData[];
}

export default function CardDetail({ card, onClose, onNavigate, relatedCards }: CardDetailProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  // Find current card index in related cards
  useEffect(() => {
    const index = relatedCards.findIndex(c => c.id === card.id);
    setCurrentIndex(index >= 0 ? index : 0);
  }, [card.id, relatedCards]);

  // Handle card flip when center card changes
  useEffect(() => {
    // Flip the card when it comes into focus, then flip back after a moment
    setIsFlipped(true);
    const timer = setTimeout(() => setIsFlipped(false), 600);
    return () => clearTimeout(timer);
  }, [currentIndex]);



  useEffect(() => {
    const keyHandler = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
      if (e.key === 'ArrowLeft') onNavigate('left');
      if (e.key === 'ArrowRight') onNavigate('right');
    };
    
    document.addEventListener('keydown', keyHandler);
    return () => document.removeEventListener('keydown', keyHandler);
  }, [onClose, onNavigate]);

  // Handle click outside to close
  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Swipe/drag navigation
  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
    setDragOffset(0);
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const deltaX = e.clientX - dragStart.x;
    setDragOffset(deltaX);
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    
    const threshold = 50; // Lower threshold for more responsive swiping
    
    if (Math.abs(dragOffset) > threshold) {
      if (dragOffset > 0 && currentIndex > 0) {
        onNavigate('left');
      } else if (dragOffset < 0 && currentIndex < relatedCards.length - 1) {
        onNavigate('right');
      }
    }
    
    setIsDragging(false);
    setDragOffset(0);
  };

  // Touch events for mobile
  const handleTouchStart = (e: React.TouchEvent) => {
    const touch = e.touches[0];
    setIsDragging(true);
    setDragStart({ x: touch.clientX, y: touch.clientY });
    setDragOffset(0);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (!isDragging) return;
    const touch = e.touches[0];
    const deltaX = touch.clientX - dragStart.x;
    setDragOffset(deltaX);
  };

  const handleTouchEnd = () => {
    handleMouseUp();
  };

  // Get visible cards for Cash App linear perspective layout
  const getVisibleCards = () => {
    const totalCards = relatedCards.length;
    const visibleCount = Math.min(5, totalCards); // Show 5 cards like Cash App (2 left, center, 2 right)
    const centerIndex = Math.floor(visibleCount / 2);
    
    const startIndex = Math.max(0, Math.min(currentIndex - centerIndex, totalCards - visibleCount));
    const endIndex = Math.min(totalCards, startIndex + visibleCount);
    
    return relatedCards.slice(startIndex, endIndex).map((card, index) => {
      const absoluteIndex = startIndex + index;
      const relativePosition = absoluteIndex - currentIndex; // -2, -1, 0, 1, 2
      const isCenter = absoluteIndex === currentIndex;
      
      return {
        ...card,
        absoluteIndex,
        relativePosition,
        isCenter
      };
    });
  };

  const visibleCards = getVisibleCards();

  return (
    <div 
      className="fixed inset-0 z-50 bg-black backdrop-blur-sm flex items-center justify-center"
      onClick={handleBackdropClick}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* Close button */}
      <button
        onClick={(e) => {
          e.stopPropagation();
          onClose();
        }}
        className="absolute top-8 right-8 z-60 w-12 h-12 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300"
      >
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
        </svg>
      </button>

      {/* Card necklace layout - Cash App style */}
      <div 
        ref={containerRef}
        className="relative w-full h-full flex items-center justify-center cursor-grab active:cursor-grabbing pointer-events-none"
        style={{
          transform: `translateX(${dragOffset}px)`,
          transition: isDragging ? 'none' : 'transform 0.3s ease-out'
        }}
      >
                {visibleCards.map((cardData) => {
          const { relativePosition, isCenter } = cardData;
          
          // Cash App linear perspective positioning
          const baseSpacing = 200; // Horizontal spacing between cards
          const x = relativePosition * baseSpacing; // Simple linear positioning
          const y = 0; // Keep all cards at same vertical level
          
          // Perspective scaling - center card largest, others get smaller with distance
          const scale = isCenter ? 1 : Math.max(0.7, 1 - Math.abs(relativePosition) * 0.15);
          const opacity = 1; // All cards fully visible, no opacity reduction
          const zIndex = isCenter ? 50 : 50 - Math.abs(relativePosition);
          
          // Slight rotation for perspective depth
          const rotation = relativePosition * 8; // Subtle rotation based on position
          
          return (
                        <div
              key={cardData.id}
              className="absolute transition-all duration-500 ease-out pointer-events-auto"
              style={{
                transform: `
                  translate(${x}px, ${y}px) 
                  scale(${scale}) 
                  rotate(${rotation}deg)
                `,
                opacity,
                zIndex,
                transformOrigin: 'center center'
              }}
              onMouseDown={handleMouseDown}
              onMouseMove={handleMouseMove}
              onTouchStart={handleTouchStart}
              onTouchMove={handleTouchMove}
              onTouchEnd={handleTouchEnd}
              onClick={(e) => {
                e.stopPropagation();
                if (!isCenter && !isDragging) {
                  // Direct navigation to clicked card
                  const direction = relativePosition > 0 ? 'right' : 'left';
                  onNavigate(direction);
                }
              }}
            >
                             {/* Card */}
               <div className={`
                 relative rounded-2xl overflow-hidden backdrop-blur-xl border border-white/20 shadow-xl
                 ${isCenter ? 'w-80 h-80 shadow-2xl shadow-black/50' : 'w-64 h-64 shadow-lg shadow-black/30'}
                 transition-all duration-500
               `}
               style={{
                 transform: isCenter && isFlipped ? 'rotateY(180deg)' : 'rotateY(0deg)',
                 transformStyle: 'preserve-3d'
               }}>
                {/* Background image */}
                <div className="absolute inset-0">
                  <img
                    src={cardData.img}
                    alt={cardData.title}
                    className="w-full h-full object-cover"
                    draggable={false}
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
                </div>

                {/* Content overlay */}
                <div className={`absolute inset-0 flex flex-col justify-end ${
                  isCenter ? 'p-8' : 'p-4'
                }`}>
                  <div className="text-center">
                    <h2 className={`font-bold text-white mb-2 transition-all duration-500 ${
                      isCenter ? 'text-3xl' : 'text-lg'
                    }`}>
                      {cardData.title}
                    </h2>
                    <p className={`text-white/80 transition-all duration-500 ${
                      isCenter ? 'text-lg' : 'text-sm'
                    }`}>
                      {cardData.subtitle}
                    </p>
                  </div>
                </div>


              </div>
            </div>
          );
        })}

        {/* Center card additional info */}
        <div className="absolute -bottom-28 left-1/2 -translate-x-1/2 text-center z-60 pointer-events-auto">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl px-6 py-4 border border-white/20"
               onClick={(e) => e.stopPropagation()}>
            <p className="text-white/60 text-sm mb-1">Category</p>
            <p className="text-white font-semibold text-base">
              {relatedCards[currentIndex]?.category || relatedCards[currentIndex]?.title}
            </p>
          </div>
        </div>
      </div>



      {/* Card counter */}
      <div className="absolute top-8 left-8 bg-white/10 backdrop-blur-md rounded-xl px-6 py-3 border border-white/20"
           onClick={(e) => e.stopPropagation()}>
        <p className="text-white/80 text-base">
          {currentIndex + 1} of {relatedCards.length}
        </p>
      </div>
    </div>
  );
}
