import React from 'react';
import { Deck } from './components/Deck';
import { MergeBin } from './components/MergeBin';
import { useDeckStore } from './store/deckStore';
import { Card } from './types';

export const App: React.FC = () => {
  const { decks, selectedDeck, setSelectedDeck } = useDeckStore();

  const handleMerge = (cards: Card[]) => {
    console.log('Merged cards:', cards);
    // Additional merge logic can be added here
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4">
          <h1 className="text-3xl font-bold text-gray-900">Deck Cards System</h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {decks.map(deck => (
            <Deck
              key={deck.id}
              deck={deck}
              onUpgrade={deck.tier !== 'master' ? () => console.log('Upgrade deck:', deck.id) : undefined}
            />
          ))}
        </div>

        <div className="mt-8">
          <MergeBin onMerge={handleMerge} />
        </div>
      </main>
    </div>
  );
};
