import React, { useState, useEffect, useRef } from "react";

interface CardData {
  id: string;
  img: string;
  title: string;
  subtitle: string;
  category?: string;
}

interface CardDetailProps {
  card: CardData;
  onClose: () => void;
  onNavigate: (direction: 'left' | 'right') => void;
  relatedCards: CardData[];
}

export default function CardDetail({ card, onClose, onNavigate, relatedCards }: CardDetailProps) {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Find current card index in related cards
  useEffect(() => {
    const index = relatedCards.findIndex(c => c.id === card.id);
    setCurrentIndex(index >= 0 ? index : 0);
  }, [card.id, relatedCards]);

  const handleKeyDown = (e: KeyboardEvent) => {
    if (e.key === 'Escape') onClose();
    if (e.key === 'ArrowLeft') onNavigate('left');
    if (e.key === 'ArrowRight') onNavigate('right');
  };

  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, []);

  const getVisibleCards = () => {
    const totalCards = relatedCards.length;
    const visibleRange = 5; // Show 5 cards total (2 left, center, 2 right)
    const centerIndex = Math.floor(visibleRange / 2);
    
    const startIndex = Math.max(0, currentIndex - centerIndex);
    const endIndex = Math.min(totalCards, startIndex + visibleRange);
    
    return relatedCards.slice(startIndex, endIndex).map((card, index) => ({
      ...card,
      position: index - (currentIndex - startIndex), // -2, -1, 0, 1, 2
      isCenter: index === (currentIndex - startIndex)
    }));
  };

  const visibleCards = getVisibleCards();

  return (
    <div className="fixed inset-0 z-50 bg-black/80 backdrop-blur-sm flex items-center justify-center">
      {/* Close button */}
      <button
        onClick={onClose}
        className="absolute top-8 right-8 z-60 w-12 h-12 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300"
      >
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
        </svg>
      </button>

      {/* Navigation arrows */}
      <button
        onClick={() => onNavigate('left')}
        className="absolute left-8 top-1/2 -translate-y-1/2 z-60 w-16 h-16 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300 hover:scale-110"
        disabled={currentIndex === 0}
      >
        <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
      </button>

      <button
        onClick={() => onNavigate('right')}
        className="absolute right-8 top-1/2 -translate-y-1/2 z-60 w-16 h-16 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300 hover:scale-110"
        disabled={currentIndex === relatedCards.length - 1}
      >
        <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>
      </button>

      {/* Card showcase */}
      <div className="w-full max-w-7xl mx-auto px-16 flex items-center justify-center gap-8">
        {visibleCards.map((cardData, index) => {
          const { position, isCenter } = cardData;
          
          return (
            <div
              key={cardData.id}
              className={`
                relative transition-all duration-700 ease-out cursor-pointer
                ${isCenter ? 
                  'scale-100 z-30 opacity-100' : 
                  Math.abs(position) === 1 ? 
                    'scale-75 z-20 opacity-70' : 
                    'scale-50 z-10 opacity-40'
                }
                ${position < 0 ? '-translate-x-4' : position > 0 ? 'translate-x-4' : ''}
              `}
              onClick={() => {
                if (!isCenter) {
                  const targetIndex = relatedCards.findIndex(c => c.id === cardData.id);
                  const direction = targetIndex > currentIndex ? 'right' : 'left';
                  onNavigate(direction);
                }
              }}
              style={{
                transform: `
                  scale(${isCenter ? 1 : Math.abs(position) === 1 ? 0.75 : 0.5})
                  translateX(${position * (isCenter ? 0 : position < 0 ? -16 : 16)}px)
                  translateZ(${isCenter ? 0 : Math.abs(position) * -100}px)
                `,
                filter: isCenter ? 'none' : 'brightness(0.7)',
              }}
            >
              {/* Card container */}
              <div className={`
                relative rounded-3xl overflow-hidden backdrop-blur-xl border border-white/20
                ${isCenter ? 'w-96 h-96' : 'w-80 h-80'}
                transition-all duration-700
                ${isCenter ? 'shadow-2xl shadow-black/50' : 'shadow-lg shadow-black/30'}
              `}>
                {/* Background image */}
                <div className="absolute inset-0">
                  <img
                    src={cardData.img}
                    alt={cardData.title}
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
                </div>

                {/* Content overlay */}
                <div className="absolute inset-0 p-8 flex flex-col justify-end">
                  <div className="text-center">
                    <h2 className={`
                      font-bold text-white mb-2 transition-all duration-700
                      ${isCenter ? 'text-4xl' : 'text-2xl'}
                    `}>
                      {cardData.title}
                    </h2>
                    <p className={`
                      text-white/80 transition-all duration-700
                      ${isCenter ? 'text-xl' : 'text-base'}
                    `}>
                      {cardData.subtitle}
                    </p>
                  </div>
                </div>

                {/* Glassmorphic overlay for non-center cards */}
                {!isCenter && (
                  <div className="absolute inset-0 bg-white/5 backdrop-blur-sm" />
                )}
              </div>

              {/* Center card additional details */}
              {isCenter && (
                <div className="absolute -bottom-20 left-1/2 -translate-x-1/2 text-center">
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl px-6 py-4 border border-white/20">
                    <p className="text-white/60 text-sm mb-2">Category</p>
                    <p className="text-white font-semibold">{cardData.category || cardData.title}</p>
                  </div>
                </div>
              )}
            </div>
          );
        })}
      </div>

      {/* Progress indicator */}
      <div className="absolute bottom-8 left-1/2 -translate-x-1/2 flex gap-2">
        {relatedCards.map((_, index) => (
          <button
            key={index}
            onClick={() => {
              const direction = index > currentIndex ? 'right' : 'left';
              const steps = Math.abs(index - currentIndex);
              for (let i = 0; i < steps; i++) {
                setTimeout(() => onNavigate(direction), i * 100);
              }
            }}
            className={`
              w-2 h-2 rounded-full transition-all duration-300
              ${index === currentIndex ? 
                'bg-white w-8' : 
                'bg-white/30 hover:bg-white/50'
              }
            `}
          />
        ))}
      </div>

      {/* Card counter */}
      <div className="absolute top-8 left-8 bg-white/10 backdrop-blur-md rounded-xl px-4 py-2 border border-white/20">
        <p className="text-white/80 text-sm">
          {currentIndex + 1} of {relatedCards.length}
        </p>
      </div>
    </div>
  );
}
