import React from 'react';
import { Card as CardType } from '../types';
import { useDeckStore } from '../store/deckStore';

interface CardProps {
  card: CardType;
  onClick?: () => void;
  isDraggable?: boolean;
  isSelected?: boolean;
  onSelect?: () => void;
}

export const Card: React.FC<CardProps> = ({ 
  card, 
  onClick, 
  isDraggable = false,
  isSelected = false,
  onSelect 
}) => {
  const { decks, activeDeckId } = useDeckStore();
  const activeDeck = decks.find(d => d.id === activeDeckId);

  const getLevelColor = (level: CardType['level']) => {
    switch (level) {
      case 'purple':
        return 'bg-purple-500';
      case 'yellow':
        return 'bg-yellow-500';
      case 'red':
        return 'bg-red-500';
      default:
        return 'bg-gray-500';
    }
  };

  const handleClick = () => {
    if (onSelect) {
      onSelect();
    } else if (onClick) {
      onClick();
    }
  };

  return (
    <div
      className={`relative w-48 h-64 rounded-lg shadow-lg transform transition-all duration-200 hover:scale-105 cursor-pointer ${
        isDraggable ? 'cursor-move' : ''
      } ${isSelected ? 'ring-4 ring-blue-500' : ''}`}
      onClick={handleClick}
      draggable={isDraggable}
    >
      <div className={`absolute top-0 left-0 w-full h-2 ${getLevelColor(card.level)} rounded-t-lg`} />
      <div className="p-4 h-full flex flex-col">
        <h3 className="text-lg font-bold mb-2">{card.name}</h3>
        <p className="text-sm text-gray-600 mb-2">{card.category}</p>
        <p className="text-sm text-gray-500 flex-grow">{card.description}</p>
        <div className="mt-2 text-xs text-gray-400">
          Earned: {new Date(card.dateEarned).toLocaleDateString()}
        </div>
        {activeDeck && !activeDeck.isArchived && (
          <div className="absolute top-2 right-2">
            <div className={`w-4 h-4 rounded-full ${isSelected ? 'bg-blue-500' : 'bg-gray-300'}`} />
          </div>
        )}
      </div>
    </div>
  );
};
