import React, { useState } from "react";

// Generate 100 sample cards with random Unsplash thumbnails
const cards = Array.from({ length: 100 }, (_, i) => ({
  id: i,
  img: `https://source.unsplash.com/collection/1163637/200x200?sig=${i}`,
  title: `Card ${i + 1}`
}));

interface DeckCardProps {
  img: string;
  title: string;
}

const DeckCard: React.FC<DeckCardProps> = ({ img, title }) => {
  return (
    <div className="deck-card">
      <img 
        src={img} 
        alt={title}
        onError={(e) => {
          // Fallback to a placeholder if image fails to load
          e.currentTarget.src = `https://via.placeholder.com/80x80/cccccc/ffffff?text=${encodeURIComponent(title.slice(0, 1))}`;
        }}
      />
      <div className="title">{title}</div>
    </div>
  );
};

export default function InfiniteCanvas() {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [start, setStart] = useState({ x: 0, y: 0 });

  function onMouseDown(e: React.MouseEvent) {
    setDragging(true);
    setStart({ x: e.clientX - offset.x, y: e.clientY - offset.y });
  }

  function onMouseMove(e: React.MouseEvent) {
    if (!dragging) return;
    setOffset({ x: e.clientX - start.x, y: e.clientY - start.y });
  }

  function onMouseUp() {
    setDragging(false);
  }

  return (
    <div 
      className="InfiniteCanvas"
      onMouseDown={onMouseDown}
      onMouseMove={onMouseMove}
      onMouseUp={onMouseUp}
      onMouseLeave={onMouseUp} // Stop dragging when mouse leaves the canvas
    >
      <div
        className="CardGrid"
        style={{ 
          transform: `translate(${offset.x}px, ${offset.y}px)`,
          width: '200vw', // Make grid wider than viewport
          height: '200vh' // Make grid taller than viewport
        }}
      >
        {cards.map(c => (
          <DeckCard key={c.id} img={c.img} title={c.title} />
        ))}
      </div>
    </div>
  );
}
