import React from 'react';
import { useDeckStore } from './store/deckStore';
import { Deck } from './components/Deck';
import { MergeBin } from './components/MergeBin';
import { DECK_TIERS } from './types';

const App: React.FC = () => {
  const { decks, activeDeckId, setActiveDeck, upgradeDeck } = useDeckStore();

  const handleUpgrade = (deckId: string, currentTier: string) => {
    const tiers = Object.keys(DECK_TIERS);
    const currentIndex = tiers.indexOf(currentTier);
    if (currentIndex < tiers.length - 1) {
      upgradeDeck(deckId, tiers[currentIndex + 1] as any);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <h1 className="text-3xl font-bold text-gray-900">Deck Gallery</h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 gap-8">
          {decks.map((deck) => (
            <Deck
              key={deck.id}
              deck={deck}
              onUpgrade={() => handleUpgrade(deck.id, deck.tier)}
            />
          ))}
        </div>

        {decks.length === 0 && (
          <div className="text-center py-12">
            <h2 className="text-2xl font-semibold text-gray-600">
              No decks available
            </h2>
            <p className="mt-2 text-gray-500">
              Create your first deck to start collecting cards!
            </p>
          </div>
        )}
      </main>

      <MergeBin />
    </div>
  );
};

export default App;
