import React, { useState } from 'react';
import { Deck as DeckType, DECK_TIERS, Card as CardType } from '../types';
import { Card } from './Card';
import { useDeckStore } from '../store/deckStore';

interface DeckProps {
  deck: DeckType;
  onUpgrade?: () => void;
}

export const Deck: React.FC<DeckProps> = ({ deck, onUpgrade }) => {
  const { removeCardFromDeck, removeCardFromAllDecks, addCardToDeck } = useDeckStore();
  const [selectedCards, setSelectedCards] = useState<string[]>([]);
  const [isDragOver, setIsDragOver] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const tierInfo = DECK_TIERS[deck.tier];
  const capacityPercentage = (deck.cards.length / tierInfo.capacity) * 100;

  const handleCardSelect = (cardId: string) => {
    setSelectedCards(prev => {
      if (prev.includes(cardId)) {
        return prev.filter(id => id !== cardId);
      }
      if (prev.length >= 3) {
        return prev;
      }
      return [...prev, cardId];
    });
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    const cardData = e.dataTransfer.getData('card');
    if (!cardData) return;

    try {
      const card: CardType = JSON.parse(cardData);
      const allowedLevels = getAllowedCardLevels(deck.tier);
      
      if (allowedLevels.includes(card.level)) {
        setIsDragOver(true);
        setErrorMessage(null);
      } else {
        setErrorMessage(`This deck only accepts ${allowedLevels.join(' and ')} cards`);
      }
    } catch {
      // Invalid card data, ignore
    }
  };

  const handleDragLeave = (e: React.DragEvent) => {
    setIsDragOver(false);
    setErrorMessage(null);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    setErrorMessage(null);

    const cardData = e.dataTransfer.getData('card');
    if (!cardData) return;

    const card: CardType = JSON.parse(cardData);
    
    // Don't allow dropping if deck is archived
    if (deck.isArchived) {
      setErrorMessage('Cannot add cards to archived deck');
      return;
    }

    // Check if the card level is allowed in this deck
    const allowedLevels = getAllowedCardLevels(deck.tier);
    if (!allowedLevels.includes(card.level)) {
      setErrorMessage(`This deck only accepts ${allowedLevels.join(' and ')} cards`);
      return;
    }

    // Check if deck is full
    if (deck.cards.length >= tierInfo.capacity) {
      setErrorMessage('Deck is full');
      return;
    }

    // Check if card is already in this deck
    if (deck.cards.some(c => c.id === card.id)) {
      setErrorMessage('Card already exists in this deck');
      return;
    }

    // Remove the card from all decks and add it to this deck
    removeCardFromAllDecks(card.id);
    addCardToDeck(deck.id, card);
  };

  const handleDragStart = (e: React.DragEvent, card: CardType) => {
    e.dataTransfer.setData('card', JSON.stringify(card));
    // Set the drag effect to move
    e.dataTransfer.effectAllowed = 'move';
  };

  return (
    <div 
      className={`bg-white rounded-lg shadow-md p-3 transition-colors duration-200 ${
        isDragOver ? 'bg-blue-50' : ''
      }`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      <div className="flex justify-between items-center mb-2">
        <div>
          <h2 className="text-lg font-bold">{deck.name}</h2>
          <p className="text-xs text-gray-600 capitalize">{deck.tier} Deck</p>
        </div>
        <div className="text-right">
          <p className="text-xs text-gray-500">
            {deck.cards.length} / {tierInfo.capacity}
          </p>
          <div className="w-24 h-1.5 bg-gray-200 rounded-full mt-0.5">
            <div
              className="h-full bg-blue-500 rounded-full"
              style={{ width: `${capacityPercentage}%` }}
            />
          </div>
        </div>
      </div>

      {errorMessage && (
        <div className="mb-2 p-2 bg-red-50 text-red-600 rounded text-xs">
          {errorMessage}
        </div>
      )}

      <div className="grid grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-2">
        {deck.cards.map((card) => (
          <Card
            key={card.id}
            card={card}
            isDraggable={!deck.isArchived}
            isSelected={selectedCards.includes(card.id)}
            onSelect={() => !deck.isArchived && handleCardSelect(card.id)}
            onClick={() => !deck.isArchived && removeCardFromDeck(deck.id, card.id)}
            onDragStart={(e) => handleDragStart(e, card)}
          />
        ))}
      </div>

      {!deck.isArchived && deck.cards.length >= tierInfo.capacity && (
        <div className="mt-2 p-2 bg-yellow-50 rounded text-xs">
          <p className="text-yellow-800">
            Deck is full! {onUpgrade && 'Consider upgrading to store more cards.'}
          </p>
          {onUpgrade && (
            <button
              onClick={onUpgrade}
              className="mt-1 px-2 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors text-xs"
            >
              Upgrade Deck
            </button>
          )}
        </div>
      )}
    </div>
  );
};

// Helper function to get allowed card levels for a deck tier
const getAllowedCardLevels = (tier: DeckType['tier']): CardType['level'][] => {
  switch (tier) {
    case 'starter':
      return ['purple'];
    case 'student':
      return ['purple', 'yellow'];
    case 'scholars':
      return ['purple', 'yellow', 'red'];
    case 'master':
      return ['red'];
    default:
      return ['purple'];
  }
};
