import React, { useState } from 'react';
import { useDeckStore } from './store/deckStore';
import { Deck } from './components/Deck';
import { MergeBin } from './components/MergeBin';
import { MergeLog } from './components/MergeLog';
import { DECK_TIERS } from './types';

const App: React.FC = () => {
  const { decks, activeDeckId, setActiveDeck, upgradeDeck, shuffleDecks } = useDeckStore();
  const [showMergeLog, setShowMergeLog] = useState(false);

  const handleUpgrade = (deckId: string, currentTier: string) => {
    const tiers = Object.keys(DECK_TIERS);
    const currentIndex = tiers.indexOf(currentTier);
    if (currentIndex < tiers.length - 1) {
      upgradeDeck(deckId, tiers[currentIndex + 1] as any);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold text-gray-800">Deck Management</h1>
          <div className="flex gap-2">
            <button
              onClick={() => setShowMergeLog(true)}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors text-sm"
            >
              Merge Log
            </button>
            <button
              onClick={shuffleDecks}
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 transition-colors text-sm"
              title="Reset all decks to original sample data (Development Only)"
            >
              Reset to Sample Data
            </button>
            <button
              onClick={() => {/* TODO: Implement purchase new deck */}}
              className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600 transition-colors text-sm"
            >
              Purchase New Deck
            </button>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {decks.map((deck) => (
            <Deck
              key={deck.id}
              deck={deck}
              onUpgrade={() => handleUpgrade(deck.id, deck.tier)}
            />
          ))}
        </div>
      </div>
      <MergeBin />
      {showMergeLog && <MergeLog onClose={() => setShowMergeLog(false)} />}
    </div>
  );
};

export default App;
