import React, { useState, useMemo } from "react";
import "./InfiniteCanvas.css";

// Widget card templates with high-quality background images
const cardTemplates = [
  { 
    img: "https://images.unsplash.com/photo-1635070041078-e363dbe005cb?w=400&h=400&fit=crop&crop=entropy", 
    title: "Mathematics",
