    title: "Geometry",
    subtitle: "Sacred Patterns"
  },
  { 
    img: "https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?w=400&h=400&fit=crop&crop=entropy", 
    title: "Music",
    subtitle: "Audio Production"
  },
  { 
    img: "https://images.unsplash.com/photo-1446776653964-20c1d3a81b06?w=400&h=400&fit=crop&crop=entropy", 
    title: "Astronomy",
    subtitle: "Space & Stars"
  },
  { 
    img: "https://images.unsplash.com/photo-1611974789855-9c2a0a7236a3?w=400&h=400&fit=crop&crop=entropy", 
    title: "Finance",
    subtitle: "Market Analytics"
  },
  { 
    img: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=400&h=400&fit=crop&crop=entropy", 
