  },
  { 
    img: "https://images.unsplash.com/photo-1559757148-5c350d0d3c56?w=400&h=400&fit=crop&crop=entropy", 
    title: "Health",
    subtitle: "Wellness & Fitness"
  },
  { 
    img: "https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?w=400&h=400&fit=crop&crop=entropy", 
    title: "Sports",
