import React, { useState, useRef } from "react";
import "./InfiniteCanvas.css";

// 1) Nine cards matching your mockup image
const cards = [
  { id: 1, img: "/assets/thumbnails/math.png", title: "Mathematics" },
  { id: 2, img: "/assets/thumbnails/programming.png", title: "Programming" },
  { id: 3, img: "/assets/thumbnails/languages.png", title: "Languages" },
  { id: 4, img: "/assets/thumbnails/history.png", title: "History" },
  { id: 5, img: "/assets/thumbnails/quantum.png", title: "Quantum Mechanics" },
  { id: 6, img: "/assets/thumbnails/chemistry.png", title: "Chemistry" },
  { id: 7, img: "/assets/thumbnails/geometry.png", title: "Geometry" },
  { id: 8, img: "/assets/thumbnails/music.png", title: "Music" },
  { id: 9, img: "/assets/thumbnails/astronomy.png", title: "Astronomy" },
];

export default function InfiniteCanvas() {
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [start, setStart] = useState({ x: 0, y: 0 });

  function onMouseDown(e) {
    setDragging(true);
    setStart({ x: e.clientX - offset.x, y: e.clientY - offset.y });
  }
  function onMouseMove(e) {
    if (!dragging) return;
    setOffset({ x: e.clientX - start.x, y: e.clientY - start.y });
  }
  function onMouseUp() {
    setDragging(false);
  }

  return (
    <div
      className="InfiniteCanvas"
      onMouseDown={onMouseDown}
      onMouseMove={onMouseMove}
      onMouseUp={onMouseUp}
    >
      <div
        className="CardGrid"
        style={{ transform: `translate(${offset.x}px, ${offset.y}px)` }}
      >
        {cards.map((c) => (
          <div key={c.id} className="DeckCard">
            <img src={c.img} alt={c.title} />
            <div className="title">{c.title}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
