import React from 'react';
import { Deck as DeckType, DECK_TIERS } from '../types';
import { Card } from './Card';
import { useDeckStore } from '../store/deckStore';

interface DeckProps {
  deck: DeckType;
  onUpgrade?: () => void;
}

export const Deck: React.FC<DeckProps> = ({ deck, onUpgrade }) => {
  const { removeCardFromDeck } = useDeckStore();
  const tierInfo = DECK_TIERS[deck.tier];
  const capacityPercentage = (deck.cards.length / tierInfo.capacity) * 100;

  return (
    <div className="bg-white rounded-xl shadow-lg p-6">
      <div className="flex justify-between items-center mb-4">
        <div>
          <h2 className="text-2xl font-bold">{deck.name}</h2>
          <p className="text-gray-600 capitalize">{deck.tier} Deck</p>
        </div>
        <div className="text-right">
          <p className="text-sm text-gray-500">
            {deck.cards.length} / {tierInfo.capacity} cards
          </p>
          <div className="w-32 h-2 bg-gray-200 rounded-full mt-1">
            <div
              className="h-full bg-blue-500 rounded-full"
              style={{ width: `${capacityPercentage}%` }}
            />
          </div>
        </div>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {deck.cards.map((card) => (
          <Card
            key={card.id}
            card={card}
            isDraggable={!deck.isArchived}
            onClick={() => !deck.isArchived && removeCardFromDeck(deck.id, card.id)}
          />
        ))}
      </div>

      {!deck.isArchived && deck.cards.length >= tierInfo.capacity && (
        <div className="mt-4 p-4 bg-yellow-50 rounded-lg">
          <p className="text-yellow-800">
            Deck is full! {onUpgrade && 'Consider upgrading to store more cards.'}
          </p>
          {onUpgrade && (
            <button
              onClick={onUpgrade}
              className="mt-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
            >
              Upgrade Deck
            </button>
          )}
        </div>
      )}
    </div>
  );
};
