import React, { useState, useEffect, useRef, useCallback } from "react";

interface CardData {
  id: string;
  img: string;
  title: string;
  subtitle: string;
  category?: string;
}

interface CardDetailProps {
  card: CardData;
  onClose: () => void;
  onNavigate: (direction: 'left' | 'right') => void;
  relatedCards: CardData[];
}

export default function CardDetail({ card, onClose, onNavigate, relatedCards }: CardDetailProps) {
  // 3D Globe rotation system - now supports free camera rotation
  const [cameraRotation, setCameraRotation] = useState({ x: -10, y: 0 }); // Camera rotation in degrees
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const containerRef = useRef<HTMLDivElement>(null);

  const globeRadius = 400; // Radius of the globe

  // All cards exist as fixed objects arranged around a 3D globe's equator
  const allCardsIn3D = relatedCards.map((cardData, index) => {
    const totalCards = relatedCards.length;
    
    // Calculate position around the equator (Y = 0 for equator)
    const angle = (index / totalCards) * 2 * Math.PI; // Evenly space cards around circle
    const x = Math.cos(angle) * globeRadius; // X position on circle
    const y = 0; // All cards on equator (Y = 0)
    const z = Math.sin(angle) * globeRadius; // Z position on circle
    
    // Calculate rotation to face center of globe
    const rotationY = (angle * 180 / Math.PI) + 180; // Face inward toward center
    
    return {
      ...cardData,
      absoluteIndex: index,
      positionInSpace: index,
      x3d: x,
      y3d: y,
      z3d: z,
      angle: angle,
      rotationY: rotationY // Store rotation for facing center
    };
  });

  // Function to rotate to focus on a specific card
  const focusOnCard = useCallback((targetCard: any) => {
    // Calculate camera rotation to face the target card
    const targetAngle = targetCard.angle * 180 / Math.PI;
    setCameraRotation(prev => ({
      ...prev,
      y: -targetAngle // Rotate camera to face the card
    }));
  }, []);

  // Initialize camera to focus on clicked card
  useEffect(() => {
    const targetCard = allCardsIn3D.find(c => c.id === card.id);
    if (targetCard) {
      focusOnCard(targetCard);
    }
  }, [card.id, focusOnCard, allCardsIn3D]);

  useEffect(() => {
    const keyHandler = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
      if (e.key === 'ArrowLeft' || e.key === 'ArrowRight') {
        // Find current focused card and move to next/previous
        const currentIndex = allCardsIn3D.findIndex(c => c.id === card.id);
        const totalCards = allCardsIn3D.length;
        
        let nextIndex;
        if (e.key === 'ArrowRight') {
          nextIndex = (currentIndex + 1) % totalCards;
        } else {
          nextIndex = (currentIndex - 1 + totalCards) % totalCards;
        }
        
        const nextCard = allCardsIn3D[nextIndex];
        focusOnCard(nextCard);
      }
    };
    
    document.addEventListener('keydown', keyHandler);
    return () => document.removeEventListener('keydown', keyHandler);
  }, [onClose, focusOnCard, allCardsIn3D, card.id]);

  // Handle click outside to close
  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Free camera rotation with mouse drag
  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
    setDragOffset({ x: 0, y: 0 });
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    
    const deltaX = e.clientX - dragStart.x;
    const deltaY = e.clientY - dragStart.y;
    
    // Convert mouse movement to rotation with sensitivity
    const sensitivity = 0.5;
    const rotationDeltaX = deltaY * sensitivity; // Mouse Y controls camera X rotation (up/down)
    const rotationDeltaY = deltaX * sensitivity; // Mouse X controls camera Y rotation (left/right)
    
    setDragOffset({ x: rotationDeltaX, y: rotationDeltaY });
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    
    // Apply the drag offset to the camera rotation
    setCameraRotation(prev => ({
      x: Math.max(-90, Math.min(90, prev.x + dragOffset.x)), // Limit vertical rotation
      y: prev.y + dragOffset.y // Allow free horizontal rotation
    }));
    
    setIsDragging(false);
    setDragOffset({ x: 0, y: 0 });
  };

  // Touch events for mobile
  const handleTouchStart = (e: React.TouchEvent) => {
    const touch = e.touches[0];
    setIsDragging(true);
    setDragStart({ x: touch.clientX, y: touch.clientY });
    setDragOffset({ x: 0, y: 0 });
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (!isDragging) return;
    const touch = e.touches[0];
    
    const deltaX = touch.clientX - dragStart.x;
    const deltaY = touch.clientY - dragStart.y;
    
    const sensitivity = 0.5;
    const rotationDeltaX = deltaY * sensitivity;
    const rotationDeltaY = deltaX * sensitivity;
    
    setDragOffset({ x: rotationDeltaX, y: rotationDeltaY });
  };

  const handleTouchEnd = () => {
    handleMouseUp();
  };

  return (
    <div 
      className="fixed inset-0 z-50 bg-black backdrop-blur-sm flex items-center justify-center"
      onClick={handleBackdropClick}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
      style={{
        perspective: '1200px', // 3D perspective for the entire scene
        perspectiveOrigin: 'center center'
      }}
    >
      {/* Close button */}
      <button
        onClick={(e) => {
          e.stopPropagation();
          onClose();
        }}
        className="absolute top-8 right-8 z-60 w-12 h-12 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300"
      >
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
        </svg>
      </button>

      {/* 3D Globe centered in screen with free camera rotation */}
      <div 
        ref={containerRef}
        className="relative w-full h-full flex items-center justify-center cursor-grab active:cursor-grabbing"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
        style={{
          transform: `
            rotateX(${cameraRotation.x + dragOffset.x}deg)
            rotateY(${cameraRotation.y + dragOffset.y}deg)
          `,
          transition: isDragging ? 'none' : 'transform 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94)',
          transformStyle: 'preserve-3d'
        }}
      >
        {/* Globe wireframe for visual reference */}
        <div 
          className="absolute pointer-events-none"
          style={{
            width: `${globeRadius * 2}px`,
            height: `${globeRadius * 2}px`,
            borderRadius: '50%',
            border: '2px solid rgba(255,255,255,0.1)',
            transform: 'translate(-50%, -50%)',
            left: '50%',
            top: '50%'
          }}
        />
        
        {/* Equator line */}
        <div 
          className="absolute pointer-events-none"
          style={{
            width: `${globeRadius * 2}px`,
            height: '2px',
            background: 'rgba(255,255,255,0.2)',
            transform: 'translate(-50%, -50%)',
            left: '50%',
            top: '50%'
          }}
        />

        {allCardsIn3D.map((cardData) => {
          // Use pre-calculated globe positions
          const x = cardData.x3d;
          const y = cardData.y3d;
          const z = cardData.z3d;
          
          // Check if this is the default/clicked card (should be facing viewer)
          const isDefaultCard = cardData.id === card.id;
          
          // Calculate distance from viewer (positive Z = closer to viewer after camera rotation)
          const distanceFromViewer = globeRadius - z;
          
          // Enhanced perspective scaling - dramatic difference between front and back
          let scale;
          if (z > globeRadius * 0.5) { // Front cards (z > 200)
            // Front cards (very close to viewer) - large and prominent
            scale = Math.max(1.0, 1.2 - (globeRadius - z) / globeRadius);
          } else if (z > 0) {
            // Front-side cards - medium size
            scale = Math.max(0.7, 1.0 - (globeRadius - z) / (globeRadius + 100));
          } else if (z > -globeRadius * 0.5) { // Back-side cards (z > -200)
            // Back-side cards - smaller
            scale = Math.max(0.4, 0.7 - Math.abs(z) / (globeRadius + 100));
          } else {
            // Far back cards - very small
            scale = Math.max(0.2, 0.4 - Math.abs(z + globeRadius * 0.5) / globeRadius);
          }
          
          // Enhanced opacity - cards at back fade significantly
          let opacity;
          if (z > globeRadius * 0.25) { // Front cards (z > 100)
            opacity = 1.0; // Front cards fully visible
          } else if (z > -globeRadius * 0.25) { // Side cards (z > -100)
            opacity = Math.max(0.6, 1.0 - Math.abs(z) / (globeRadius * 0.5)); // Side cards fade slightly
          } else {
            opacity = Math.max(0.2, 0.6 - Math.abs(z + globeRadius * 0.25) / (globeRadius * 0.75)); // Back cards very faded
          }
          
          // Z-index for proper layering
          const zIndex = Math.round(100 + z / (globeRadius / 100));
          
          // Cards face toward the center of the globe
          const rotationY = cardData.rotationY;
          const rotationX = 0;
          
          return (
            <div
              key={cardData.id}
              className="absolute transition-all duration-500 ease-out"
              style={{
                transform: `
                  translate3d(${x}px, ${y}px, ${z}px) 
                  scale(${scale}) 
                  rotateY(${rotationY}deg) 
                  rotateX(${rotationX}deg)
                `,
                opacity,
                zIndex,
                transformOrigin: 'center center',
                transformStyle: 'preserve-3d'
              }}
            >
              {/* Card with enhanced styling for default card */}
              <div 
                className={`relative w-56 h-56 rounded-2xl overflow-hidden backdrop-blur-xl transition-all duration-500 ${
                  isDefaultCard 
                    ? 'border-2 border-white/40 shadow-2xl shadow-white/20' // Default card gets special border and glow
                    : 'border border-white/20 shadow-xl shadow-black/40'     // Other cards get standard styling
                }`}
                style={{
                  transformStyle: 'preserve-3d'
                }}
              >
                {/* Background image */}
                <div className="absolute inset-0">
                  <img
                    src={cardData.img}
                    alt={cardData.title}
                    className="w-full h-full object-cover"
                    draggable={false}
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
                </div>

                {/* Content overlay with enhanced styling for default card */}
                <div className="absolute inset-0 flex flex-col justify-end p-6">
                  <div className="text-center">
                    <h2 className={`font-bold text-white mb-2 transition-all duration-500 ${
                      isDefaultCard ? 'text-3xl drop-shadow-lg' : 'text-2xl'
                    }`}>
                      {cardData.title}
                    </h2>
                    <p className={`text-white/80 transition-all duration-500 ${
                      isDefaultCard ? 'text-lg drop-shadow-md' : 'text-base'
                    }`}>
                      {cardData.subtitle}
                    </p>
                  </div>
                </div>


              </div>
            </div>
          );
        })}

        {/* Current card info */}
        <div className="absolute -bottom-32 left-1/2 -translate-x-1/2 text-center z-60 pointer-events-none">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl px-6 py-4 border border-white/20">
            <p className="text-white/60 text-sm mb-1">Featured Card</p>
            <p className="text-white font-semibold text-lg mb-2">
              {card.title}
            </p>
            <p className="text-white/70 text-sm">
              Swipe to rotate • Arrow keys to navigate
            </p>
          </div>
        </div>
      </div>

      {/* Card counter */}
      <div className="absolute top-8 left-8 bg-white/10 backdrop-blur-md rounded-xl px-6 py-3 border border-white/20 pointer-events-none">
        <p className="text-white/80 text-base">
          {relatedCards.length} Cards on Globe
        </p>
      </div>
    </div>
  );
}
