import React, { useState, useEffect, useRef } from "react";

interface CardData {
  id: string;
  img: string;
  title: string;
  subtitle: string;
  category?: string;
}

interface CardDetailProps {
  card: CardData;
  onClose: () => void;
  onNavigate: (direction: 'left' | 'right') => void;
  relatedCards: CardData[];
}

export default function CardDetail({ card, onClose, onNavigate, relatedCards }: CardDetailProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);
  const containerRef = useRef<HTMLDivElement>(null);

  // Find current card index in related cards
  useEffect(() => {
    const index = relatedCards.findIndex(c => c.id === card.id);
    setCurrentIndex(index >= 0 ? index : 0);
  }, [card.id, relatedCards]);

  const handleKeyDown = (e: KeyboardEvent) => {
    if (e.key === 'Escape') onClose();
    if (e.key === 'ArrowLeft') onNavigate('left');
    if (e.key === 'ArrowRight') onNavigate('right');
  };

  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Handle click outside to close
  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Swipe/drag navigation
  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
    setDragOffset(0);
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging) return;
    const deltaX = e.clientX - dragStart.x;
    setDragOffset(deltaX);
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    
    const threshold = 100; // Minimum drag distance to trigger navigation
    
    if (Math.abs(dragOffset) > threshold) {
      if (dragOffset > 0 && currentIndex > 0) {
        onNavigate('left');
      } else if (dragOffset < 0 && currentIndex < relatedCards.length - 1) {
        onNavigate('right');
      }
    }
    
    setIsDragging(false);
    setDragOffset(0);
  };

  // Touch events for mobile
  const handleTouchStart = (e: React.TouchEvent) => {
    const touch = e.touches[0];
    setIsDragging(true);
    setDragStart({ x: touch.clientX, y: touch.clientY });
    setDragOffset(0);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (!isDragging) return;
    const touch = e.touches[0];
    const deltaX = touch.clientX - dragStart.x;
    setDragOffset(deltaX);
  };

  const handleTouchEnd = () => {
    handleMouseUp();
  };

  const currentCard = relatedCards[currentIndex];

  return (
    <div 
      className="fixed inset-0 z-50 bg-black/80 backdrop-blur-sm flex items-center justify-center"
      onClick={handleBackdropClick}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      {/* Close button */}
      <button
        onClick={onClose}
        className="absolute top-8 right-8 z-60 w-12 h-12 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center hover:bg-white/20 transition-all duration-300"
      >
        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
        </svg>
      </button>

      {/* Single centered card - Cash App style */}
      <div 
        ref={containerRef}
        className="relative w-[500px] h-[500px] cursor-grab active:cursor-grabbing"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
        style={{
          transform: `translateX(${dragOffset}px)`,
          transition: isDragging ? 'none' : 'transform 0.3s ease-out'
        }}
      >
        {/* Main card */}
        <div className="relative w-full h-full rounded-3xl overflow-hidden backdrop-blur-xl border border-white/20 shadow-2xl shadow-black/50">
          {/* Background image */}
          <div className="absolute inset-0">
            <img
              src={currentCard.img}
              alt={currentCard.title}
              className="w-full h-full object-cover"
              draggable={false}
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
          </div>

          {/* Content overlay */}
          <div className="absolute inset-0 p-12 flex flex-col justify-end">
            <div className="text-center">
              <h2 className="font-bold text-white mb-4 text-5xl">
                {currentCard.title}
              </h2>
              <p className="text-white/80 text-2xl">
                {currentCard.subtitle}
              </p>
            </div>
          </div>
        </div>

        {/* Card info below */}
        <div className="absolute -bottom-24 left-1/2 -translate-x-1/2 text-center">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl px-8 py-6 border border-white/20">
            <p className="text-white/60 text-base mb-2">Category</p>
            <p className="text-white font-semibold text-lg">{currentCard.category || currentCard.title}</p>
          </div>
        </div>

        {/* Drag hint */}
        {!isDragging && (
          <div className="absolute -bottom-40 left-1/2 -translate-x-1/2 text-center">
            <p className="text-white/40 text-sm">
              ← Drag to navigate →
            </p>
          </div>
        )}
      </div>

      {/* Minimal progress indicator */}
      <div className="absolute bottom-8 left-1/2 -translate-x-1/2 flex gap-3">
        {relatedCards.map((_, index) => (
          <div
            key={index}
            className={`
              h-1 rounded-full transition-all duration-300
              ${index === currentIndex ? 
                'bg-white w-12' : 
                'bg-white/30 w-4'
              }
            `}
          />
        ))}
      </div>

      {/* Card counter */}
      <div className="absolute top-8 left-8 bg-white/10 backdrop-blur-md rounded-xl px-6 py-3 border border-white/20">
        <p className="text-white/80 text-base">
          {currentIndex + 1} of {relatedCards.length}
        </p>
      </div>
    </div>
  );
}
