import React, { useState } from 'react';
import { Card, CardLevel } from '../types';
import { useDeckStore } from '../store/deckStore';
import { ArchiveBoxIcon } from '@heroicons/react/24/outline';

interface MergeBinProps {
  onMerge: (cards: Card[]) => void;
}

export const MergeBin: React.FC<MergeBinProps> = ({ onMerge }) => {
  const [cards, setCards] = useState<Card[]>([]);
  const [mergedCard, setMergedCard] = useState<Card | null>(null);
  const { archiveCard, addCardToDeck } = useDeckStore();

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const cardData = e.dataTransfer.getData('application/json');
    if (!cardData) return;

    const card: Card = JSON.parse(cardData);

    // Check if card is already in the bin
    if (cards.some(c => c.id === card.id)) return;

    // Check if we already have 3 cards
    if (cards.length >= 3) return;

    // Check if the card level matches existing cards
    if (cards.length > 0 && cards[0].level !== card.level) return;

    // Archive the card and add it to the merge bin
    archiveCard(card);
    setCards(prev => [...prev, card]);

    // If we now have 3 cards, merge them
    if (cards.length === 2) {
      handleMerge([...cards, card]);
    }
  };

  const handleMerge = (cardsToMerge: Card[]) => {
    if (cardsToMerge.length !== 3) return;

    const newCard: Card = {
      id: `merged-${Date.now()}`,
      name: `${cardsToMerge[0].name} (Merged)`,
      description: `Merged from ${cardsToMerge.map(c => c.name).join(', ')}`,
      level: cardsToMerge[0].level,
      color: cardsToMerge[0].level,
      isArchived: false
    };

    setMergedCard(newCard);
    setCards([]);
    onMerge(cardsToMerge);
  };

  const handleRemoveCard = (cardId: string) => {
    setCards(prev => prev.filter(card => card.id !== cardId));
  };

  const handleDragStart = (e: React.DragEvent, card: Card) => {
    e.dataTransfer.setData('application/json', JSON.stringify(card));
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragEnd = (e: React.DragEvent, card: Card) => {
    // If the drop was successful (not cancelled), remove the card from the merge bin
    if (e.dataTransfer.dropEffect === 'move') {
      setCards(prev => prev.filter(c => c.id !== card.id));
    }
  };

  const getCardStyle = (card: Card) => {
    const colorMap: Record<CardLevel, string> = {
      purple: 'bg-purple-100 border-purple-300',
      yellow: 'bg-yellow-100 border-yellow-300',
      red: 'bg-red-100 border-red-300'
    };
    return colorMap[card.color];
  };

  return (
    <div
      className="w-full p-4 bg-gray-100 rounded-lg border-2 border-dashed border-gray-300 min-h-[200px]"
      onDragOver={handleDragOver}
      onDrop={handleDrop}
    >
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          <ArchiveBoxIcon className="w-6 h-6 text-gray-500" />
          <h3 className="text-lg font-semibold">Merge Bin</h3>
        </div>
        <div className="text-sm text-gray-500">
          {cards.length}/3 cards
        </div>
      </div>

      <div className="grid grid-cols-3 gap-2">
        {cards.map(card => (
          <div
            key={card.id}
            className="relative"
            draggable
            onDragStart={(e) => handleDragStart(e, card)}
            onDragEnd={(e) => handleDragEnd(e, card)}
          >
            <div className={`p-2 rounded-lg ${getCardStyle(card)} cursor-move`}>
              <div className="text-sm font-medium">{card.name}</div>
              <button
                onClick={() => handleRemoveCard(card.id)}
                className="absolute top-1 right-1 text-gray-500 hover:text-gray-700"
              >
                ×
              </button>
            </div>
          </div>
        ))}
      </div>

      {mergedCard && (
        <div className="mt-4">
          <div className="text-sm font-medium text-gray-700 mb-2">Merged Card:</div>
          <div
            className={`p-2 rounded-lg ${getCardStyle(mergedCard)}`}
            draggable
            onDragStart={(e) => handleDragStart(e, mergedCard)}
            onDragEnd={(e) => handleDragEnd(e, mergedCard)}
          >
            <div className="text-sm font-medium">{mergedCard.name}</div>
            <div className="text-xs text-gray-600">{mergedCard.description}</div>
          </div>
        </div>
      )}
    </div>
  );
};
